client/src/pages/pricing.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Header } from "@/components/Header";
import { Check, Music, Zap, Crown, User, LogOut } from "lucide-react";
import { Link } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import type { SubscriptionPlan } from "@shared/schema";

export default function Pricing() {
  const { user } = useAuth();
  const { data: plans = [], isLoading } = useQuery<SubscriptionPlan[]>({
    queryKey: ["/api/plans"],
  });

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-indigo-900">
        <div className="container mx-auto px-4 py-16">
          <div className="text-center mb-16">
            <div className="h-12 bg-gray-700 rounded animate-pulse mb-4 max-w-md mx-auto" />
            <div className="h-6 bg-gray-700 rounded animate-pulse max-w-2xl mx-auto" />
          </div>
          <div className="grid gap-8 md:grid-cols-3">
            {[1, 2, 3].map((i) => (
              <div key={i} className="h-96 bg-gray-700 rounded-lg animate-pulse" />
            ))}
          </div>
        </div>
      </div>
    );
  }

  const getPlanIcon = (planName: string) => {
    if (planName.toLowerCase().includes('premium')) return Crown;
    if (planName.toLowerCase().includes('basic')) return Zap;
    return Music;
  };

  const getPlanColor = (planName: string) => {
    if (planName.toLowerCase().includes('premium')) return 'from-yellow-600 to-orange-600';
    if (planName.toLowerCase().includes('basic')) return 'from-purple-600 to-indigo-600';
    return 'from-green-600 to-teal-600';
  };

  const formatPrice = (price: string) => {
    const num = parseFloat(price);
    return num === 0 ? 'Free' : `$${num}`;
  };

  const getFeatures = (plan: SubscriptionPlan): string[] => {
    if (!plan.features) return [];
    if (typeof plan.features === 'string') {
      try {
        return JSON.parse(plan.features);
      } catch {
        return (plan.features as string).split('\n').map((f: string) => f.replace('â€¢ ', '').trim()).filter(Boolean);
      }
    }
    return Array.isArray(plan.features) ? plan.features : [];
  };

  return (
    <div className="min-h-screen text-white">
      {/* Header */}
      <Header currentPage="pricing" />

      <div className="min-h-screen bg-gradient-to-br from-gray-900 via-purple-900 to-indigo-900">

      <div className="container mx-auto px-4 py-16">
        {/* Header */}
        <div className="text-center mb-16">
          <h1 className="text-4xl md:text-6xl font-bold text-white mb-6">
            Choose Your <span className="text-transparent bg-clip-text bg-gradient-to-r from-purple-400 to-pink-400">Creative Plan</span>
          </h1>
          <p className="text-xl text-gray-300 max-w-2xl mx-auto">
            Unlock the power of AI music generation with our flexible subscription plans. 
            Create amazing music that fits your needs and budget.
          </p>
        </div>

        {/* Pricing Cards */}
        <div className="grid gap-8 md:grid-cols-3 max-w-6xl mx-auto">
          {plans.map((plan) => {
            const IconComponent = getPlanIcon(plan.name);
            const gradientColor = getPlanColor(plan.name);
            const features = getFeatures(plan);
            const isPopular = plan.name.toLowerCase().includes('basic');

            return (
              <Card 
                key={plan.id}
                className={`bg-gray-800/50 border-gray-700 backdrop-blur-sm relative h-[750px] grid grid-rows-[auto_1fr_auto] ${
                  isPopular ? 'ring-2 ring-purple-500' : ''
                }`}
                data-testid={`card-plan-${plan.name.toLowerCase()}`}
              >
                {isPopular && (
                  <div className="absolute -top-3 left-1/2 transform -translate-x-1/2 z-10">
                    <span className="bg-gradient-to-r from-purple-500 to-pink-500 text-white px-3 py-1 rounded-full text-xs font-medium">
                      Most Popular
                    </span>
                  </div>
                )}

                <CardHeader className="text-center pb-8">
                  <div className={`w-16 h-16 mx-auto rounded-full bg-gradient-to-r ${gradientColor} flex items-center justify-center mb-4`}>
                    <IconComponent className="w-8 h-8 text-white" />
                  </div>

                  <CardTitle className="text-2xl font-bold text-white mb-2">
                    {plan.name}
                  </CardTitle>

                  <CardDescription className="text-gray-400 text-base mb-4">
                    {plan.description ? plan.description.split('\n')[0].replace('â€¢ ', '') : ''}
                  </CardDescription>

                  <div className="text-center">
                    <div className="text-4xl font-bold text-white mb-2">
                      {formatPrice(plan.monthlyPrice || '0')}
                      {parseFloat(plan.monthlyPrice || '0') > 0 && (
                        <span className="text-lg text-gray-400 font-normal">/month</span>
                      )}
                    </div>
                    {parseFloat(plan.yearlyPrice || '0') > 0 && (
                      <div className="text-sm text-gray-400">
                        or {formatPrice(plan.yearlyPrice || '0')} yearly
                        <span className="text-green-400 ml-1">
                          (Save {Math.round((1 - (parseFloat(plan.yearlyPrice || '0') / (parseFloat(plan.monthlyPrice || '0') * 12))) * 100)}%)
                        </span>
                      </div>
                    )}
                  </div>
                </CardHeader>

                <CardContent className="overflow-y-auto">
                  <ul className="space-y-3">
                    {features.map((feature, index) => (
                      <li key={index} className="flex items-start">
                        <Check className="w-5 h-5 text-green-400 mt-0.5 mr-3 flex-shrink-0" />
                        <span className="text-gray-300">{feature}</span>
                      </li>
                    ))}
                  </ul>
                </CardContent>

                {/* Button Footer - Grid Row */}
                <div className="p-6 bg-gray-800/30 border-t border-gray-700">
                  <Button 
                    className={`w-full bg-gradient-to-r ${gradientColor} hover:opacity-90 text-white font-medium py-3`}
                    data-testid={`button-select-${plan.name.toLowerCase()}`}
                  >
                    {plan.name === 'Free' ? 'Get Started Free' : `Choose ${plan.name}`}
                  </Button>

                  <div className="h-8 flex items-center justify-center mt-3">
                    {plan.name !== 'Free' && (
                      <p className="text-xs text-gray-500 text-center">
                        Cancel anytime. No hidden fees.
                      </p>
                    )}
                  </div>
                </div>
              </Card>
            );
          })}
        </div>

        {/* FAQ or Additional Info */}
        <div className="mt-16 text-center">
          <h3 className="text-2xl font-bold text-white mb-4">Need Help Choosing?</h3>
          <p className="text-gray-300 mb-6">
            All plans include access to our AI music generation technology. 
            Upgrade or downgrade anytime as your needs change.
          </p>
          <div className="flex justify-center space-x-4">
            <Button 
              variant="outline" 
              className="border-gray-600 text-gray-300 hover:bg-gray-800 hover:text-white bg-transparent"
              data-testid="button-contact-support"
            >
              Contact Support
            </Button>
            <Link href="/">
              <Button 
                variant="outline" 
                className="border-gray-600 text-gray-300 hover:bg-gray-800 hover:text-white bg-transparent"
                data-testid="button-try-demo"
              >
                Try Demo
              </Button>
            </Link>
          </div>
        </div>
      </div>
      </div>
    </div>
  );
}

client/src/pages/profile.tsx.content:
import { useState } from "react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Header } from "@/components/Header";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { 
  User, 
  Settings, 
  CreditCard, 
  Shield, 
  Music,
  Crown,
  Mail,
  Key,
  ChevronRight,
  LogOut
} from "lucide-react";
import type { SubscriptionPlan } from "@shared/schema";

export default function ProfilePage() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Form states
  const [emailData, setEmailData] = useState({
    newEmail: (user as any)?.email || "",
  });
  const [passwordData, setPasswordData] = useState({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });

  // Fetch subscription plans
  const { data: plans = [] } = useQuery<SubscriptionPlan[]>({
    queryKey: ["/api/plans"],
  });

  // Update email mutation
  const updateEmailMutation = useMutation({
    mutationFn: async (data: { newEmail: string }) => {
      const response = await apiRequest("PUT", "/api/user/email", data);
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/auth/user"] });
      toast({
        title: "Email updated successfully",
        description: "Your email address has been changed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to update email",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Update password mutation
  const updatePasswordMutation = useMutation({
    mutationFn: async (data: { currentPassword: string; newPassword: string }) => {
      const response = await apiRequest("PUT", "/api/user/password", data);
      return response.json();
    },
    onSuccess: () => {
      setPasswordData({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
      toast({
        title: "Password updated successfully",
        description: "Your password has been changed.",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Failed to update password",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const handleUpdateEmail = async (e: React.FormEvent) => {
    e.preventDefault();
    if (emailData.newEmail === (user as any)?.email) {
      toast({
        title: "No changes made",
        description: "The email address is the same as your current email.",
        variant: "destructive",
      });
      return;
    }
    updateEmailMutation.mutate({ newEmail: emailData.newEmail });
  };

  const handleUpdatePassword = async (e: React.FormEvent) => {
    e.preventDefault();
    if (passwordData.newPassword !== passwordData.confirmPassword) {
      toast({
        title: "Password mismatch",
        description: "New password and confirmation don't match.",
        variant: "destructive",
      });
      return;
    }
    if (passwordData.newPassword.length < 8) {
      toast({
        title: "Password too short",
        description: "Password must be at least 8 characters long.",
        variant: "destructive",
      });
      return;
    }
    updatePasswordMutation.mutate({
      currentPassword: passwordData.currentPassword,
      newPassword: passwordData.newPassword,
    });
  };

  const getCurrentPlan = () => {
    return plans.find(plan => plan.name === "Free") || plans[0]; // Default to Free plan
  };

  const currentPlan = getCurrentPlan();

  if (!user) {
    return (
      <div className="min-h-screen bg-music-dark text-white flex items-center justify-center">
        <div className="text-center">
          <h1 className="text-2xl font-bold mb-4">Please sign in to access your profile</h1>
          <Button onClick={() => window.location.href = "/auth"}>
            Sign In
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-music-dark text-white">
      <Header currentPage="profile" />

      <div className="max-w-4xl mx-auto px-4 py-8">
        {/* Profile Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold mb-2">Account Settings</h1>
          <p className="text-gray-400">Manage your account, subscription, and preferences</p>
        </div>

        <div className="grid gap-6">
          {/* Profile Information */}
          <Card className="bg-music-secondary border-gray-700">
            <CardHeader>
              <CardTitle className="flex items-center">
                <User className="mr-2 h-5 w-5 text-music-accent" />
                Profile Information
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <label className="text-sm font-medium text-gray-300">First Name</label>
                  <div className="mt-1 text-white bg-music-dark px-3 py-2 rounded-md border border-gray-600">
                    {(user as any)?.firstName || 'Not set'}
                  </div>
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-300">Last Name</label>
                  <div className="mt-1 text-white bg-music-dark px-3 py-2 rounded-md border border-gray-600">
                    {(user as any)?.lastName || 'Not set'}
                  </div>
                </div>
              </div>
              <div>
                <label className="text-sm font-medium text-gray-300">Current Email</label>
                <div className="mt-1 text-white bg-music-dark px-3 py-2 rounded-md border border-gray-600 flex items-center">
                  <Mail className="mr-2 h-4 w-4 text-gray-400" />
                  {(user as any)?.email || 'Not set'}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Current Subscription */}
          <Card className="bg-music-secondary border-gray-700">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Crown className="mr-2 h-5 w-5 text-music-accent" />
                Current Subscription
              </CardTitle>
            </CardHeader>
            <CardContent>
              {currentPlan ? (
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <div>
                      <h3 className="text-lg font-semibold text-white">{currentPlan.name} Plan</h3>
                      <p className="text-gray-400">{currentPlan.description}</p>
                    </div>
                    <Badge 
                      variant={currentPlan.name === "Free" ? "secondary" : "default"}
                      className={currentPlan.name === "Free" 
                        ? "bg-gray-600 text-gray-200" 
                        : "bg-gradient-to-r from-music-purple to-music-blue text-white"
                      }
                    >
                      {currentPlan.name}
                    </Badge>
                  </div>

                  <div className="grid md:grid-cols-2 gap-4 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-400">Monthly Generations:</span>
                      <span className="text-white font-medium">{currentPlan.maxGenerations}</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-400">Monthly Price:</span>
                      <span className="text-white font-medium">
                        {(currentPlan.monthlyPrice && parseFloat(currentPlan.monthlyPrice) > 0) ? `$${currentPlan.monthlyPrice}/mo` : "Free"}
                      </span>
                    </div>
                  </div>

                  <Separator className="bg-gray-600" />

                  <div className="flex items-center justify-between">
                    <div>
                      <p className="text-sm text-gray-400">Want to upgrade or downgrade your plan?</p>
                    </div>
                    <Button
                      variant="outline"
                      className="border-music-accent text-music-accent hover:bg-music-accent hover:text-white"
                      onClick={() => window.location.href = "/pricing"}
                      data-testid="button-manage-subscription"
                    >
                      <CreditCard className="mr-2 h-4 w-4" />
                      Change Plan
                      <ChevronRight className="ml-2 h-4 w-4" />
                    </Button>
                  </div>
                </div>
              ) : (
                <p className="text-gray-400">No subscription information available</p>
              )}
            </CardContent>
          </Card>

          {/* Update Email */}
          <Card className="bg-music-secondary border-gray-700">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Mail className="mr-2 h-5 w-5 text-music-blue" />
                Update Email Address
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleUpdateEmail} className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-gray-300">New Email Address</label>
                  <Input
                    type="email"
                    value={emailData.newEmail}
                    onChange={(e) => setEmailData({ newEmail: e.target.value })}
                    className="mt-1 bg-music-dark border-gray-600 text-white"
                    required
                    data-testid="input-new-email"
                  />
                </div>
                <Button
                  type="submit"
                  disabled={updateEmailMutation.isPending}
                  className="bg-music-blue hover:bg-blue-600"
                  data-testid="button-update-email"
                >
                  {updateEmailMutation.isPending ? "Updating..." : "Update Email"}
                </Button>
              </form>
            </CardContent>
          </Card>

          {/* Update Password */}
          <Card className="bg-music-secondary border-gray-700">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Key className="mr-2 h-5 w-5 text-music-green" />
                Update Password
              </CardTitle>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleUpdatePassword} className="space-y-4">
                <div>
                  <label className="text-sm font-medium text-gray-300">Current Password</label>
                  <Input
                    type="password"
                    value={passwordData.currentPassword}
                    onChange={(e) => setPasswordData({ ...passwordData, currentPassword: e.target.value })}
                    className="mt-1 bg-music-dark border-gray-600 text-white"
                    required
                    data-testid="input-current-password"
                  />
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-300">New Password</label>
                  <Input
                    type="password"
                    value={passwordData.newPassword}
                    onChange={(e) => setPasswordData({ ...passwordData, newPassword: e.target.value })}
                    className="mt-1 bg-music-dark border-gray-600 text-white"
                    required
                    minLength={8}
                    data-testid="input-new-password"
                  />
                </div>
                <div>
                  <label className="text-sm font-medium text-gray-300">Confirm New Password</label>
                  <Input
                    type="password"
                    value={passwordData.confirmPassword}
                    onChange={(e) => setPasswordData({ ...passwordData, confirmPassword: e.target.value })}
                    className="mt-1 bg-music-dark border-gray-600 text-white"
                    required
                    minLength={8}
                    data-testid="input-confirm-password"
                  />
                </div>
                <Button
                  type="submit"
                  disabled={updatePasswordMutation.isPending}
                  className="bg-music-green hover:bg-green-600"
                  data-testid="button-update-password"
                >
                  {updatePasswordMutation.isPending ? "Updating..." : "Update Password"}
                </Button>
              </form>
            </CardContent>
          </Card>

          {/* Account Security */}
          <Card className="bg-music-secondary border-gray-700">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Shield className="mr-2 h-5 w-5 text-music-purple" />
                Account Security
              </CardTitle>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="flex items-center justify-between">
                <div>
                  <h4 className="font-medium text-white">Login Sessions</h4>
                  <p className="text-sm text-gray-400">Manage your active login sessions</p>
                </div>
                <Button variant="outline" size="sm" className="border-gray-600 text-gray-300">
                  View Sessions
                </Button>
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    </div>
  );
}

