import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { Pencil, Plus, Trash2, DollarSign } from "lucide-react";
import type { SubscriptionPlan } from "@shared/schema";

interface PlanFormData {
  name: string;
  description: string;
  features: string[];
  monthlyPrice: string;
  yearlyPrice: string;
  monthlyPriceId: string;
  yearlyPriceId: string;
  maxGenerations: number;
  generationsNumber: number;
  sortOrder: number;
  isActive: boolean;
}

export function AdminPlanManagement() {
  const [editingPlan, setEditingPlan] = useState<SubscriptionPlan | null>(null);
  const [showCreateDialog, setShowCreateDialog] = useState(false);
  const [showEditDialog, setShowEditDialog] = useState(false);
  const [formData, setFormData] = useState<PlanFormData>({
    name: "",
    description: "",
    features: [],
    monthlyPrice: "0",
    yearlyPrice: "0",
    monthlyPriceId: "",
    yearlyPriceId: "",
    maxGenerations: 5,
    generationsNumber: 5,
    sortOrder: 0,
    isActive: true,
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: plans = [], isLoading } = useQuery<SubscriptionPlan[]>({
    queryKey: ["/api/admin/plans"],
  });

  const createPlanMutation = useMutation({
    mutationFn: async (data: PlanFormData) => {
      return await apiRequest("/api/admin/plans", "POST", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/plans"] });
      setShowCreateDialog(false);
      resetForm();
      toast({
        title: "Success",
        description: "Subscription plan created successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to create subscription plan",
        variant: "destructive",
      });
    },
  });

  const updatePlanMutation = useMutation({
    mutationFn: async ({ id, data }: { id: string; data: PlanFormData }) => {
      return await apiRequest(`/api/admin/plans/${id}`, "PUT", data);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/plans"] });
      setShowEditDialog(false);
      setEditingPlan(null);
      resetForm();
      toast({
        title: "Success",
        description: "Subscription plan updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update subscription plan",
        variant: "destructive",
      });
    },
  });

  const deletePlanMutation = useMutation({
    mutationFn: async (id: string) => {
      return await apiRequest(`/api/admin/plans/${id}`, "DELETE");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/plans"] });
      toast({
        title: "Success",
        description: "Subscription plan deleted successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to delete subscription plan",
        variant: "destructive",
      });
    },
  });

  const resetForm = () => {
    setFormData({
      name: "",
      description: "",
      features: [],
      monthlyPrice: "0",
      yearlyPrice: "0",
      monthlyPriceId: "",
      yearlyPriceId: "",
      maxGenerations: 5,
      generationsNumber: 5,
      sortOrder: 0,
      isActive: true,
    });
    setEditingPlan(null);
  };

  const handleCreatePlan = (e: React.FormEvent) => {
    e.preventDefault();
    createPlanMutation.mutate(formData);
  };

  const handleUpdatePlan = (e: React.FormEvent) => {
    e.preventDefault();
    if (!editingPlan) return;
    updatePlanMutation.mutate({ id: editingPlan.id, data: formData });
  };

  const handleEditPlan = (plan: SubscriptionPlan) => {
    setEditingPlan(plan);
    setFormData({
      name: plan.name,
      description: plan.description || "",
      features: Array.isArray(plan.features) ? plan.features : [],
      monthlyPrice: plan.monthlyPrice || "0",
      yearlyPrice: plan.yearlyPrice || "0",
      monthlyPriceId: plan.monthlyPriceId || "",
      yearlyPriceId: plan.yearlyPriceId || "",
      maxGenerations: plan.maxGenerations || 5,
      generationsNumber: plan.generationsNumber || 5,
      sortOrder: plan.sortOrder || 0,
      isActive: plan.isActive,
    });
    setShowEditDialog(true);
  };

  const handleDeletePlan = (id: string) => {
    if (confirm("Are you sure you want to delete this subscription plan?")) {
      deletePlanMutation.mutate(id);
    }
  };

  const openCreateDialog = () => {
    resetForm();
    setShowCreateDialog(true);
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-4">
        <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
        <div className="grid gap-4 md:grid-cols-2">
          {[1, 2, 3].map((i) => (
            <div key={i} className="h-48 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white">Subscription Plans</h2>
          <p className="text-gray-400 mt-1">Manage subscription plans and pricing</p>
        </div>
        <Dialog open={showCreateDialog} onOpenChange={setShowCreateDialog}>
          <DialogTrigger asChild>
            <Button 
              onClick={openCreateDialog}
              className="bg-purple-600 hover:bg-purple-700" 
              data-testid="button-create-plan"
            >
              <Plus className="w-4 h-4 mr-2" />
              Create Plan
            </Button>
          </DialogTrigger>
          <DialogContent className="bg-gray-900 border-gray-700 max-w-2xl">
            <DialogHeader>
              <DialogTitle className="text-white">Create Subscription Plan</DialogTitle>
              <DialogDescription className="text-gray-400">
                Create a new subscription plan with pricing and features
              </DialogDescription>
            </DialogHeader>
            <form onSubmit={handleCreatePlan} className="space-y-4">
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Plan Name</label>
                  <Input 
                    value={formData.name}
                    onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="Basic, Pro, Premium"
                    required
                    data-testid="input-plan-name"
                  />
                </div>
                <div>
                  <label className="text-gray-200 text-sm font-medium">Sort Order</label>
                  <Input 
                    type="number"
                    value={formData.sortOrder}
                    onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="0"
                    data-testid="input-sort-order"
                  />
                </div>
              </div>

              <div>
                <label className="text-gray-200 text-sm font-medium">Description</label>
                <Textarea 
                  value={formData.description}
                  onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  placeholder="â€¢ Feature 1&#10;â€¢ Feature 2&#10;â€¢ Feature 3"
                  rows={4}
                  data-testid="textarea-description"
                />
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Monthly Price ($)</label>
                  <Input 
                    type="number"
                    step="0.01"
                    value={formData.monthlyPrice}
                    onChange={(e) => setFormData({ ...formData, monthlyPrice: e.target.value })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="9.99"
                    data-testid="input-monthly-price"
                  />
                </div>
                <div>
                  <label className="text-gray-200 text-sm font-medium">Yearly Price ($)</label>
                  <Input 
                    type="number"
                    step="0.01"
                    value={formData.yearlyPrice}
                    onChange={(e) => setFormData({ ...formData, yearlyPrice: e.target.value })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="99.99"
                    data-testid="input-yearly-price"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Max Generations</label>
                  <Input 
                    type="number"
                    value={formData.maxGenerations}
                    onChange={(e) => setFormData({ ...formData, maxGenerations: parseInt(e.target.value) || 5 })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="5"
                    min="1"
                    data-testid="input-max-generations"
                  />
                </div>
                <div>
                  <label className="text-gray-200 text-sm font-medium">Generations Number</label>
                  <Input 
                    type="number"
                    value={formData.generationsNumber}
                    onChange={(e) => setFormData({ ...formData, generationsNumber: parseInt(e.target.value) || 5 })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="5"
                    min="1"
                    data-testid="input-generations-number"
                  />
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Monthly Stripe Price ID</label>
                  <Input 
                    value={formData.monthlyPriceId}
                    onChange={(e) => setFormData({ ...formData, monthlyPriceId: e.target.value })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="price_..."
                    data-testid="input-monthly-price-id"
                  />
                </div>
                <div>
                  <label className="text-gray-200 text-sm font-medium">Yearly Stripe Price ID</label>
                  <Input 
                    value={formData.yearlyPriceId}
                    onChange={(e) => setFormData({ ...formData, yearlyPriceId: e.target.value })}
                    className="bg-gray-800 border-gray-600 text-white mt-1"
                    placeholder="price_..."
                    data-testid="input-yearly-price-id"
                  />
                </div>
              </div>

              <div className="flex items-center space-x-4">
                <label className="flex items-center space-x-2">
                  <input
                    type="checkbox"
                    checked={formData.isActive}
                    onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
                    className="rounded"
                    data-testid="checkbox-is-active"
                  />
                  <span className="text-gray-200">Active</span>
                </label>
              </div>

              <div className="flex justify-end space-x-2">
                <Button 
                  type="button" 
                  variant="outline" 
                  onClick={() => setShowCreateDialog(false)}
                  className="border-gray-600 text-gray-300"
                  data-testid="button-cancel-create"
                >
                  Cancel
                </Button>
                <Button 
                  type="submit" 
                  disabled={createPlanMutation.isPending}
                  className="bg-purple-600 hover:bg-purple-700"
                  data-testid="button-submit-create"
                >
                  {createPlanMutation.isPending ? "Creating..." : "Create Plan"}
                </Button>
              </div>
            </form>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {plans.map((plan: SubscriptionPlan) => (
          <Card key={plan.id} className="bg-gray-800 border-gray-700" data-testid={`card-plan-${plan.id}`}>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-white">{plan.name}</CardTitle>
                <div className="flex items-center space-x-2">
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => handleEditPlan(plan)}
                    className="h-8 w-8 p-0 text-gray-400 hover:text-white"
                    data-testid={`button-edit-${plan.id}`}
                  >
                    <Pencil className="w-4 h-4" />
                  </Button>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => handleDeletePlan(plan.id)}
                    className="h-8 w-8 p-0 text-red-400 hover:text-red-300"
                    data-testid={`button-delete-${plan.id}`}
                  >
                    <Trash2 className="w-4 h-4" />
                  </Button>
                </div>
              </div>
              <CardDescription className="text-gray-400">
                Sort Order: {plan.sortOrder} â€¢ {plan.isActive ? "Active" : "Inactive"}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-400 flex items-center">
                    <DollarSign className="w-4 h-4 mr-1" />
                    Monthly
                  </span>
                  <span className="text-white font-semibold" data-testid={`text-monthly-price-${plan.id}`}>
                    ${plan.monthlyPrice}/mo
                  </span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-400 flex items-center">
                    <DollarSign className="w-4 h-4 mr-1" />
                    Yearly
                  </span>
                  <span className="text-white font-semibold" data-testid={`text-yearly-price-${plan.id}`}>
                    ${plan.yearlyPrice}/yr
                  </span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-400">Max Generations</span>
                  <span className="text-white font-semibold" data-testid={`text-max-generations-${plan.id}`}>
                    {plan.maxGenerations}
                  </span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-400">Max Generations</span>
                  <span className="text-white font-semibold" data-testid={`text-max-generations-${plan.id}`}>
                    {plan.maxGenerations}
                  </span>
                </div>
                <div className="flex items-center justify-between text-sm">
                  <span className="text-gray-400">Generations Number</span>
                  <span className="text-white font-semibold" data-testid={`text-generations-number-${plan.id}`}>
                    {plan.generationsNumber}
                  </span>
                </div>
                {plan.description && (
                  <div className="text-sm text-gray-300">
                    <div className="font-medium mb-1">Features:</div>
                    <div className="whitespace-pre-line text-xs">
                      {plan.description}
                    </div>
                  </div>
                )}
                {(plan.monthlyPriceId || plan.yearlyPriceId) && (
                  <div className="text-xs text-gray-500 space-y-1">
                    {plan.monthlyPriceId && (
                      <div>Monthly ID: {plan.monthlyPriceId}</div>
                    )}
                    {plan.yearlyPriceId && (
                      <div>Yearly ID: {plan.yearlyPriceId}</div>
                    )}
                  </div>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Edit Dialog */}
      <Dialog open={showEditDialog} onOpenChange={setShowEditDialog}>
        <DialogContent className="bg-gray-900 border-gray-700 max-w-2xl">
          <DialogHeader>
            <DialogTitle className="text-white">Edit Subscription Plan</DialogTitle>
            <DialogDescription className="text-gray-400">
              Update subscription plan details and pricing
            </DialogDescription>
          </DialogHeader>
          <form onSubmit={handleUpdatePlan} className="space-y-4">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-gray-200 text-sm font-medium">Plan Name</label>
                <Input 
                  value={formData.name}
                  onChange={(e) => setFormData({ ...formData, name: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  required
                  data-testid="input-edit-plan-name"
                />
              </div>
              <div>
                <label className="text-gray-200 text-sm font-medium">Sort Order</label>
                <Input 
                  type="number"
                  value={formData.sortOrder}
                  onChange={(e) => setFormData({ ...formData, sortOrder: parseInt(e.target.value) || 0 })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  data-testid="input-edit-sort-order"
                />
              </div>
            </div>

            <div>
              <label className="text-gray-200 text-sm font-medium">Description</label>
              <Textarea 
                value={formData.description}
                onChange={(e) => setFormData({ ...formData, description: e.target.value })}
                className="bg-gray-800 border-gray-600 text-white mt-1"
                rows={4}
                data-testid="textarea-edit-description"
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-gray-200 text-sm font-medium">Monthly Price ($)</label>
                <Input 
                  type="number"
                  step="0.01"
                  value={formData.monthlyPrice}
                  onChange={(e) => setFormData({ ...formData, monthlyPrice: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  data-testid="input-edit-monthly-price"
                />
              </div>
              <div>
                <label className="text-gray-200 text-sm font-medium">Yearly Price ($)</label>
                <Input 
                  type="number"
                  step="0.01"
                  value={formData.yearlyPrice}
                  onChange={(e) => setFormData({ ...formData, yearlyPrice: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  data-testid="input-edit-yearly-price"
                />
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-gray-200 text-sm font-medium">Max Generations</label>
                <Input 
                  type="number"
                  value={formData.maxGenerations}
                  onChange={(e) => setFormData({ ...formData, maxGenerations: parseInt(e.target.value) || 5 })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  min="1"
                  data-testid="input-edit-max-generations"
                />
              </div>
              <div>
                <label className="text-gray-200 text-sm font-medium">Generations Number</label>
                <Input 
                  type="number"
                  value={formData.generationsNumber}
                  onChange={(e) => setFormData({ ...formData, generationsNumber: parseInt(e.target.value) || 5 })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  min="1"
                  data-testid="input-edit-generations-number"
                />
              </div>
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-gray-200 text-sm font-medium">Monthly Stripe Price ID</label>
                <Input 
                  value={formData.monthlyPriceId}
                  onChange={(e) => setFormData({ ...formData, monthlyPriceId: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  data-testid="input-edit-monthly-price-id"
                />
              </div>
              <div>
                <label className="text-gray-200 text-sm font-medium">Yearly Stripe Price ID</label>
                <Input 
                  value={formData.yearlyPriceId}
                  onChange={(e) => setFormData({ ...formData, yearlyPriceId: e.target.value })}
                  className="bg-gray-800 border-gray-600 text-white mt-1"
                  data-testid="input-edit-yearly-price-id"
                />
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <label className="flex items-center space-x-2">
                <input
                  type="checkbox"
                  checked={formData.isActive}
                  onChange={(e) => setFormData({ ...formData, isActive: e.target.checked })}
                  className="rounded"
                  data-testid="checkbox-edit-is-active"
                />
                <span className="text-gray-200">Active</span>
              </label>
            </div>

            <div className="flex justify-end space-x-2">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => setShowEditDialog(false)}
                className="border-gray-600 text-gray-300"
                data-testid="button-cancel-edit"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                disabled={updatePlanMutation.isPending}
                className="bg-purple-600 hover:bg-purple-700"
                data-testid="button-submit-edit"
              >
                {updatePlanMutation.isPending ? "Updating..." : "Update Plan"}
              </Button>
            </div>
          </form>
        </DialogContent>
      </Dialog>

      {plans.length === 0 && (
        <Card className="bg-gray-800 border-gray-700">
          <CardContent className="flex flex-col items-center justify-center py-8">
            <DollarSign className="w-12 h-12 text-gray-500 mb-4" />
            <h3 className="text-lg font-semibold text-white mb-2">No subscription plans</h3>
            <p className="text-gray-400 text-center mb-4">
              Get started by creating your first subscription plan
            </p>
            <Button 
              onClick={openCreateDialog}
              className="bg-purple-600 hover:bg-purple-700"
              data-testid="button-create-first-plan"
            >
              <Plus className="w-4 h-4 mr-2" />
              Create Your First Plan
            </Button>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/admin/AdminSidebar.content:
import { useState } from "react";
import { useLocation } from "wouter";
import { useAdminAuth } from "@/hooks/useAdminAuth";
import { Button } from "@/components/ui/button";
import { 
  LayoutDashboard, 
  Users, 
  CreditCard, 
  Settings, 
  Music,
  LogOut,
  Menu,
  X
} from "lucide-react";
import { cn } from "@/lib/utils";

const navigationItems = [
  { id: "dashboard", label: "Dashboard", icon: LayoutDashboard, path: "/admin" },
  { id: "users", label: "User Management", icon: Users, path: "/admin/users" },
  { id: "plans", label: "Subscription Plans", icon: CreditCard, path: "/admin/plans" },
  { id: "tracks", label: "Music Tracks", icon: Music, path: "/admin/tracks" },
  { id: "settings", label: "Site Settings", icon: Settings, path: "/admin/settings" },
];

export function AdminSidebar() {
  const [, navigate] = useLocation();
  const { adminUser, logout } = useAdminAuth();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const currentPath = window.location.pathname;

  const handleLogout = async () => {
    await logout.mutateAsync();
    navigate("/admin/login");
  };

  const handleNavigation = (path: string) => {
    navigate(path);
    setIsMobileMenuOpen(false);
  };

  return (
    <>
      {/* Mobile menu button */}
      <Button
        variant="ghost"
        size="sm"
        className="fixed top-4 left-4 z-50 md:hidden bg-gray-900 hover:bg-gray-800 text-white"
        onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
        data-testid="button-mobile-menu"
      >
        {isMobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
      </Button>

      {/* Overlay */}
      {isMobileMenuOpen && (
        <div 
          className="fixed inset-0 bg-black/50 z-40 md:hidden"
          onClick={() => setIsMobileMenuOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div
        className={cn(
          "fixed left-0 top-0 z-40 h-full w-64 bg-gray-900 border-r border-gray-800 transform transition-transform duration-200 ease-in-out md:relative md:transform-none",
          isMobileMenuOpen ? "translate-x-0" : "-translate-x-full md:translate-x-0"
        )}
      >
        <div className="flex flex-col h-full">
          {/* Header */}
          <div className="p-6 border-b border-gray-800">
            <h1 className="text-xl font-bold text-white">NuMusic</h1>
            <p className="text-sm text-gray-400 mt-1">Admin Dashboard</p>
          </div>

          {/* User info */}
          <div className="p-4 border-b border-gray-800">
            <div className="flex items-center space-x-3">
              <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center">
                <span className="text-sm font-medium text-white">
                  {adminUser?.username?.charAt(0).toUpperCase()}
                </span>
              </div>
              <div>
                <p className="text-sm font-medium text-white">{adminUser?.username}</p>
                <p className="text-xs text-gray-400 capitalize">{adminUser?.role}</p>
              </div>
            </div>
          </div>

          {/* Navigation */}
          <nav className="flex-1 p-4 space-y-2">
            {navigationItems.map((item) => {
              const Icon = item.icon;
              const isActive = currentPath === item.path;

              return (
                <Button
                  key={item.id}
                  variant="ghost"
                  className={cn(
                    "w-full justify-start text-left h-auto p-3",
                    isActive 
                      ? "bg-purple-600/20 text-purple-400 border-l-2 border-purple-400" 
                      : "text-gray-300 hover:bg-gray-800 hover:text-white"
                  )}
                  onClick={() => handleNavigation(item.path)}
                  data-testid={`nav-${item.id}`}
                >
                  <Icon className="h-5 w-5 mr-3" />
                  <span>{item.label}</span>
                </Button>
              );
            })}
          </nav>

          {/* Logout */}
          <div className="p-4 border-t border-gray-800">
            <Button
              variant="ghost"
              className="w-full justify-start text-red-400 hover:bg-red-500/10 hover:text-red-300"
              onClick={handleLogout}
              disabled={logout.isPending}
              data-testid="button-logout"
            >
              <LogOut className="h-5 w-5 mr-3" />
              <span>{logout.isPending ? "Signing out..." : "Sign Out"}</span>
            </Button>
          </div>
        </div>
      </div>
    </>
  );
}

client/src/components/admin/AdminSiteSet.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { useToast } from "@/hooks/use-toast";
import { Settings, Shield, CreditCard, Webhook, Eye, EyeOff } from "lucide-react";
import type { SiteSetting } from "@shared/schema";

interface SettingFormData {
  [key: string]: string;
}

interface AdminPasswordForm {
  currentPassword: string;
  newPassword: string;
  confirmPassword: string;
}

export function AdminSiteSettings() {
  const [settingsData, setSettingsData] = useState<SettingFormData>({});
  const [passwordForm, setPasswordForm] = useState<AdminPasswordForm>({
    currentPassword: "",
    newPassword: "",
    confirmPassword: "",
  });
  const [showPasswords, setShowPasswords] = useState({
    current: false,
    new: false,
    confirm: false,
  });
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: settings = [], isLoading } = useQuery<SiteSetting[]>({
    queryKey: ["/api/admin/settings"],
  });

  // Initialize form data when settings load
  useEffect(() => {
    if (settings.length > 0) {
      const formData: SettingFormData = {};
      settings.forEach((setting) => {
        formData[setting.key] = setting.value || "";
      });
      setSettingsData(formData);
    }
  }, [settings]);

  const updateSettingMutation = useMutation({
    mutationFn: async ({ key, value }: { key: string; value: string }) => {
      return await apiRequest("/api/admin/settings", "PUT", { key, value });
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/admin/settings"] });
      toast({
        title: "Success",
        description: "Setting updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update setting",
        variant: "destructive",
      });
    },
  });

  const updatePasswordMutation = useMutation({
    mutationFn: async (data: AdminPasswordForm) => {
      return await apiRequest("/api/admin/change-password", "PUT", data);
    },
    onSuccess: () => {
      setPasswordForm({
        currentPassword: "",
        newPassword: "",
        confirmPassword: "",
      });
      toast({
        title: "Success",
        description: "Admin password updated successfully",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: error.message || "Failed to update password",
        variant: "destructive",
      });
    },
  });

  const handleSettingChange = (key: string, value: string) => {
    setSettingsData({ ...settingsData, [key]: value });
  };

  const handleSaveSetting = (key: string) => {
    updateSettingMutation.mutate({ key, value: settingsData[key] || "" });
  };

  const handlePasswordSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (passwordForm.newPassword !== passwordForm.confirmPassword) {
      toast({
        title: "Error",
        description: "New passwords do not match",
        variant: "destructive",
      });
      return;
    }
    if (passwordForm.newPassword.length < 6) {
      toast({
        title: "Error",
        description: "New password must be at least 6 characters",
        variant: "destructive",
      });
      return;
    }
    updatePasswordMutation.mutate(passwordForm);
  };

  const togglePasswordVisibility = (field: keyof typeof showPasswords) => {
    setShowPasswords({ ...showPasswords, [field]: !showPasswords[field] });
  };

  if (isLoading) {
    return (
      <div className="p-6 space-y-4">
        <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
        <div className="grid gap-4 md:grid-cols-2">
          {[1, 2, 3, 4].map((i) => (
            <div key={i} className="h-32 bg-gray-200 dark:bg-gray-700 rounded animate-pulse" />
          ))}
        </div>
      </div>
    );
  }

  const getSetting = (key: string) => {
    return settings.find((s) => s.key === key)?.value || "";
  };

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center space-x-2">
        <Settings className="w-6 h-6 text-purple-400" />
        <div>
          <h2 className="text-2xl font-bold text-white">Site Settings</h2>
          <p className="text-gray-400 mt-1">Configure site settings and integrations</p>
        </div>
      </div>

      <Tabs defaultValue="general" className="w-full">
        <TabsList className="grid w-full grid-cols-4 bg-gray-800">
          <TabsTrigger value="general" className="data-[state=active]:bg-purple-600">
            General
          </TabsTrigger>
          <TabsTrigger value="stripe" className="data-[state=active]:bg-purple-600">
            Stripe
          </TabsTrigger>
          <TabsTrigger value="webhooks" className="data-[state=active]:bg-purple-600">
            Webhooks
          </TabsTrigger>
          <TabsTrigger value="security" className="data-[state=active]:bg-purple-600">
            Security
          </TabsTrigger>
        </TabsList>

        <TabsContent value="general" className="space-y-6">
          <Card className="bg-gray-800 border-gray-700">
            <CardHeader>
              <CardTitle className="text-white flex items-center">
                <Settings className="w-5 h-5 mr-2" />
                General Site Settings
              </CardTitle>
              <CardDescription className="text-gray-400">
                Configure basic site information and branding
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Site Name</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      value={settingsData.site_name || getSetting("site_name")}
                      onChange={(e) => handleSettingChange("site_name", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="NuMusic"
                      data-testid="input-site-name"
                    />
                    <Button
                      onClick={() => handleSaveSetting("site_name")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-site-name"
                    >
                      Save
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Site Description</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      value={settingsData.site_description || getSetting("site_description")}
                      onChange={(e) => handleSettingChange("site_description", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="AI-powered music generation platform"
                      data-testid="input-site-description"
                    />
                    <Button
                      onClick={() => handleSaveSetting("site_description")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-site-description"
                    >
                      Save
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Support Email</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      type="email"
                      value={settingsData.support_email || getSetting("support_email")}
                      onChange={(e) => handleSettingChange("support_email", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="support@aimusic.studio"
                      data-testid="input-support-email"
                    />
                    <Button
                      onClick={() => handleSaveSetting("support_email")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-support-email"
                    >
                      Save
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Contact Email</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      type="email"
                      value={settingsData.contact_email || getSetting("contact_email")}
                      onChange={(e) => handleSettingChange("contact_email", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="contact@aimusic.studio"
                      data-testid="input-contact-email"
                    />
                    <Button
                      onClick={() => handleSaveSetting("contact_email")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-contact-email"
                    >
                      Save
                    </Button>
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="stripe" className="space-y-6">
          <Card className="bg-gray-800 border-gray-700">
            <CardHeader>
              <CardTitle className="text-white flex items-center">
                <CreditCard className="w-5 h-5 mr-2" />
                Stripe Integration
              </CardTitle>
              <CardDescription className="text-gray-400">
                Configure Stripe API keys for payment processing
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div className="space-y-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Stripe Publishable Key</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      value={settingsData.stripe_publishable_key || getSetting("stripe_publishable_key")}
                      onChange={(e) => handleSettingChange("stripe_publishable_key", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="pk_live_..."
                      data-testid="input-stripe-publishable-key"
                    />
                    <Button
                      onClick={() => handleSaveSetting("stripe_publishable_key")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-stripe-publishable-key"
                    >
                      Save
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Stripe Secret Key</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      type="password"
                      value={settingsData.stripe_secret_key || getSetting("stripe_secret_key")}
                      onChange={(e) => handleSettingChange("stripe_secret_key", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="sk_live_..."
                      data-testid="input-stripe-secret-key"
                    />
                    <Button
                      onClick={() => handleSaveSetting("stripe_secret_key")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-stripe-secret-key"
                    >
                      Save
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Stripe Webhook Endpoint Secret</label>
                  <div className="flex space-x-2 mt-1">
                    <Input
                      type="password"
                      value={settingsData.stripe_webhook_secret || getSetting("stripe_webhook_secret")}
                      onChange={(e) => handleSettingChange("stripe_webhook_secret", e.target.value)}
                      className="bg-gray-700 border-gray-600 text-white"
                      placeholder="whsec_..."
                      data-testid="input-stripe-webhook-secret"
                    />
                    <Button
                      onClick={() => handleSaveSetting("stripe_webhook_secret")}
                      disabled={updateSettingMutation.isPending}
                      className="bg-purple-600 hover:bg-purple-700"
                      data-testid="button-save-stripe-webhook-secret"
                    >
                      Save
                    </Button>
                  </div>
                </div>
              </div>

              <div className="mt-4 p-4 bg-blue-900/20 border border-blue-700 rounded-lg">
                <p className="text-blue-300 text-sm">
                  <strong>Note:</strong> Stripe keys are stored securely and masked for security. 
                  Make sure to use your live keys for production and test keys for development.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="webhooks" className="space-y-6">
          <Card className="bg-gray-800 border-gray-700">
            <CardHeader>
              <CardTitle className="text-white flex items-center">
                <Webhook className="w-5 h-5 mr-2" />
                Webhook Configuration
              </CardTitle>
              <CardDescription className="text-gray-400">
                Configure webhook endpoints and settings
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-6">
              {/* Webhook Setup Instructions */}
              <div className="bg-gray-700 border border-gray-600 rounded-lg p-4 space-y-4">
                <h4 className="text-md font-semibold text-blue-400">Stripe Webhook Setup Instructions</h4>
                <div className="space-y-3 text-sm text-gray-300">
                  <div>
                    <p className="font-medium text-white mb-2">1. Go to your Stripe Dashboard</p>
                    <p>Navigate to <span className="text-blue-400">Developers â†’ Webhooks</span> in your Stripe dashboard.</p>
                  </div>

                  <div>
                    <p className="font-medium text-white mb-2">2. Create a new webhook endpoint</p>
                    <p>Click "Add endpoint" and use this URL:</p>
                    <div className="bg-gray-900 border border-gray-500 rounded p-2 mt-1 font-mono text-green-400 text-xs">
                      https://numusic.app/api/webhooks/stripe
                    </div>
                  </div>

                  <div>
                    <p className="font-medium text-white mb-2">3. Select events to send</p>
                    <p>Add these essential events for subscription management:</p>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-1 mt-2">
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">customer.subscription.created</div>
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">customer.subscription.updated</div>
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">customer.subscription.deleted</div>
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">invoice.payment_succeeded</div>
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">invoice.payment_failed</div>
                      <div className="font-mono text-xs bg-gray-800 px-2 py-1 rounded text-green-400">payment_intent.succeeded</div>
                    </div>
                  </div>

                  <div>
                    <p className="font-medium text-white mb-2">4. Copy the webhook secret</p>
                    <p>After creating the webhook, copy the signing secret (whsec_...) and add it to your Stripe settings in the previous tab.</p>
                  </div>
                </div>
              </div>

              <div>
                <label className="text-gray-200 text-sm font-medium">Your Webhook Endpoint URL</label>
                <div className="flex space-x-2 mt-1">
                  <Input
                    value="https://numusic.app/api/webhooks/stripe"
                    readOnly
                    className="bg-gray-700 border-gray-600 text-gray-300 cursor-not-allowed"
                    data-testid="input-webhook-endpoint-url"
                  />
                  <Button
                    onClick={() => {
                      navigator.clipboard.writeText("https://numusic.app/api/webhooks/stripe");
                      toast({
                        title: "Copied!",
                        description: "Webhook URL copied to clipboard",
                      });
                    }}
                    className="bg-blue-600 hover:bg-blue-700"
                    data-testid="button-copy-webhook-url"
                  >
                    Copy URL
                  </Button>
                </div>
                <p className="text-xs text-gray-400 mt-1">
                  This is your webhook endpoint URL. Copy this to your Stripe dashboard.
                </p>
              </div>

              <div className="mt-4 p-4 bg-yellow-900/20 border border-yellow-700 rounded-lg">
                <p className="text-yellow-300 text-sm">
                  <strong>Important:</strong> Make sure your webhook endpoint secret is configured in the Stripe tab above. 
                  This ensures secure webhook verification and prevents unauthorized requests.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="security" className="space-y-6">
          <Card className="bg-gray-800 border-gray-700">
            <CardHeader>
              <CardTitle className="text-white flex items-center">
                <Shield className="w-5 h-5 mr-2" />
                Admin Security
              </CardTitle>
              <CardDescription className="text-gray-400">
                Change admin password and security settings
              </CardDescription>
            </CardHeader>
            <CardContent>
              <form onSubmit={handlePasswordSubmit} className="space-y-4">
                <div>
                  <label className="text-gray-200 text-sm font-medium">Current Password</label>
                  <div className="relative mt-1">
                    <Input
                      type={showPasswords.current ? "text" : "password"}
                      value={passwordForm.currentPassword}
                      onChange={(e) => setPasswordForm({ ...passwordForm, currentPassword: e.target.value })}
                      className="bg-gray-700 border-gray-600 text-white pr-10"
                      placeholder="Enter current password"
                      required
                      data-testid="input-current-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-0 top-0 h-full px-3 text-gray-400 hover:text-white"
                      onClick={() => togglePasswordVisibility("current")}
                      data-testid="button-toggle-current-password"
                    >
                      {showPasswords.current ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">New Password</label>
                  <div className="relative mt-1">
                    <Input
                      type={showPasswords.new ? "text" : "password"}
                      value={passwordForm.newPassword}
                      onChange={(e) => setPasswordForm({ ...passwordForm, newPassword: e.target.value })}
                      className="bg-gray-700 border-gray-600 text-white pr-10"
                      placeholder="Enter new password (min 6 characters)"
                      required
                      minLength={6}
                      data-testid="input-new-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-0 top-0 h-full px-3 text-gray-400 hover:text-white"
                      onClick={() => togglePasswordVisibility("new")}
                      data-testid="button-toggle-new-password"
                    >
                      {showPasswords.new ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                    </Button>
                  </div>
                </div>

                <div>
                  <label className="text-gray-200 text-sm font-medium">Confirm New Password</label>
                  <div className="relative mt-1">
                    <Input
                      type={showPasswords.confirm ? "text" : "password"}
                      value={passwordForm.confirmPassword}
                      onChange={(e) => setPasswordForm({ ...passwordForm, confirmPassword: e.target.value })}
                      className="bg-gray-700 border-gray-600 text-white pr-10"
                      placeholder="Confirm new password"
                      required
                      data-testid="input-confirm-password"
                    />
                    <Button
                      type="button"
                      variant="ghost"
                      size="sm"
                      className="absolute right-0 top-0 h-full px-3 text-gray-400 hover:text-white"
                      onClick={() => togglePasswordVisibility("confirm")}
                      data-testid="button-toggle-confirm-password"
                    >
                      {showPasswords.confirm ? <EyeOff className="w-4 h-4" /> : <Eye className="w-4 h-4" />}
                    </Button>
                  </div>
                </div>

                <div className="flex justify-end space-x-2">
                  <Button
                    type="submit"
                    disabled={updatePasswordMutation.isPending}
                    className="bg-red-600 hover:bg-red-700"
                    data-testid="button-change-password"
                  >
                    {updatePasswordMutation.isPending ? "Updating..." : "Change Password"}
                  </Button>
                </div>
              </form>

              <div className="mt-6 p-4 bg-red-900/20 border border-red-700 rounded-lg">
                <p className="text-red-300 text-sm">
                  <strong>Security Notice:</strong> Changing your password will log you out of all active sessions. 
                  Make sure to save your new password securely.
                </p>
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

