import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { LoadingSpinner } from "@/components/loading-spinner";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { WandSparkles, RotateCcw, Copy } from "lucide-react";

interface LyricsGeneratorModalProps {
  isOpen: boolean;
  onClose: () => void;
  onUseLyrics: (lyrics: string) => void;
  duration?: number; // Duration in seconds
}

export function LyricsGeneratorModal({ isOpen, onClose, onUseLyrics, duration = 60 }: LyricsGeneratorModalProps) {
  const [prompt, setPrompt] = useState("");
  const [generatedLyrics, setGeneratedLyrics] = useState("");
  const [isGenerating, setIsGenerating] = useState(false);
  const { toast } = useToast();

  const handleGenerate = async () => {
    if (!prompt.trim()) {
      toast({
        title: "Prompt required",
        description: "Please enter what your song is about.",
        variant: "destructive",
      });
      return;
    }

    setIsGenerating(true);
    try {
      const response = await apiRequest("/api/generate-lyrics", "POST", { 
        prompt: prompt.trim(),
        duration: duration 
      });
      const data = await response.json();
      setGeneratedLyrics(data.lyrics);
    } catch (error) {
      console.error('Error generating lyrics:', error);
      toast({
        title: "Generation failed",
        description: "Failed to generate lyrics. Please try again.",
        variant: "destructive",
      });
    } finally {
      setIsGenerating(false);
    }
  };

  const handleUseLyrics = () => {
    if (generatedLyrics.trim()) {
      onUseLyrics(generatedLyrics);
      onClose();
      toast({
        title: "Lyrics added",
        description: "The generated lyrics have been added to your song.",
      });
    }
  };

  const handleCopyLyrics = async () => {
    if (generatedLyrics.trim()) {
      try {
        await navigator.clipboard.writeText(generatedLyrics);
        toast({
          title: "Copied",
          description: "Lyrics copied to clipboard.",
        });
      } catch (error) {
        toast({
          title: "Copy failed",
          description: "Failed to copy lyrics to clipboard.",
          variant: "destructive",
        });
      }
    }
  };

  const handleClose = () => {
    setPrompt("");
    setGeneratedLyrics("");
    onClose();
  };

  return (
    <Dialog open={isOpen} onOpenChange={handleClose}>
      <DialogContent className="max-w-2xl bg-music-secondary border-gray-700 text-white">
        <DialogHeader>
          <DialogTitle className="flex items-center text-music-blue">
            <WandSparkles className="mr-2 h-5 w-5" />
            AI Lyrics Generator
          </DialogTitle>
          <DialogDescription className="text-gray-400">
            Generate custom lyrics for your music using AI. Describe what your song is about and get professionally structured lyrics.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-6">
          {/* Prompt Input */}
          <div className="space-y-2">
            <Label htmlFor="song-prompt" className="text-sm font-medium text-gray-300">
              What is your song about?
            </Label>
            <Input
              id="song-prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="e.g., a summer romance, overcoming challenges, city nightlife..."
              className="bg-music-dark border-gray-600 text-white placeholder-gray-400 focus:border-music-purple"
              data-testid="input-lyrics-prompt"
            />
          </div>

          {/* Generate Button */}
          <div className="flex gap-3">
            <Button
              onClick={handleGenerate}
              disabled={isGenerating || !prompt.trim()}
              className="flex-1 bg-gradient-to-r from-music-purple to-music-blue hover:from-purple-600 hover:to-blue-600"
              data-testid="button-generate-lyrics"
            >
              {isGenerating ? (
                <LoadingSpinner />
              ) : (
                <WandSparkles className="mr-2 h-4 w-4" />
              )}
              {isGenerating ? "Generating..." : "Generate Lyrics"}
            </Button>

            {generatedLyrics && (
              <Button
                onClick={handleGenerate}
                disabled={isGenerating}
                variant="outline"
                className="border-gray-600 hover:border-music-accent"
                data-testid="button-regenerate-lyrics"
              >
                <RotateCcw className="h-4 w-4" />
              </Button>
            )}
          </div>

          {/* Generated Lyrics */}
          {generatedLyrics && (
            <div className="space-y-3">
              <div className="flex items-center justify-between">
                <Label className="text-sm font-medium text-gray-300">Generated Lyrics</Label>
                <Button
                  onClick={handleCopyLyrics}
                  variant="ghost"
                  size="sm"
                  className="text-gray-400 hover:text-white"
                  data-testid="button-copy-lyrics"
                >
                  <Copy className="h-4 w-4 mr-1" />
                  Copy
                </Button>
              </div>
              <Textarea
                value={generatedLyrics}
                onChange={(e) => setGeneratedLyrics(e.target.value)}
                rows={12}
                className="bg-music-dark border-gray-600 text-white resize-none font-mono text-sm"
                data-testid="textarea-generated-lyrics"
              />
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex gap-3 pt-4 border-t border-gray-700">
            <Button
              onClick={handleClose}
              variant="outline"
              className="flex-1 border-gray-600 hover:border-gray-500 text-gray-300 hover:text-white"
              data-testid="button-cancel-lyrics"
            >
              Cancel
            </Button>
            {generatedLyrics && (
              <Button
                onClick={handleUseLyrics}
                className="flex-1 bg-music-green hover:bg-green-600"
                data-testid="button-use-lyrics"
              >
                Use These Lyrics
              </Button>
            )}
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}

server/openaiService.ts.content:
import OpenAI from "openai";

if (!process.env.OPENAI_API_KEY) {
  throw new Error('Missing required OpenAI API key: OPENAI_API_KEY');
}

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export async function generateLyrics(prompt: string, duration: number = 60): Promise<string> {
  // Determine song structure based on duration
  let structureGuidance = "";
  if (duration <= 30) {
    structureGuidance = "Create a short, concise song with just [Verse 1] and [Chorus]. Keep it simple and impactful for a 30-second track.";
  } else if (duration <= 60) {
    structureGuidance = "Create a standard pop song structure with [Verse 1], [Chorus], [Verse 2], [Chorus]. Perfect for a 1-minute track.";
  } else if (duration <= 120) {
    structureGuidance = "Create a full song structure with [Verse 1], [Chorus], [Verse 2], [Chorus], [Bridge], [Chorus]. Suitable for a 2-minute track.";
  } else {
    structureGuidance = "Create an extended song with [Verse 1], [Chorus], [Verse 2], [Chorus], [Bridge], [Chorus], [Verse 3], [Chorus]. Perfect for a longer track.";
  }

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are a professional songwriter and lyricist. Create engaging, creative lyrics based on the user's prompt. 

          The song will be ${duration} seconds long, so ${structureGuidance}

          Guidelines:
          - Structure the lyrics with clear sections like [Verse 1], [Chorus], [Verse 2], [Bridge], etc.
          - Make lyrics that flow well and are suitable for music
          - Keep it appropriate and creative
          - Match the mood and theme from the user's prompt
          - Each verse should be 4-8 lines, chorus 4-6 lines
          - Use rhyme schemes that work well for music
          - Adjust the number of sections based on the song duration

          Return only the lyrics with section markers, no additional text or explanations.`
        },
        {
          role: "user",
          content: `Write lyrics for a song about: ${prompt}`
        }
      ],
      max_tokens: 800,
      temperature: 0.8,
    });

    const lyrics = response.choices[0]?.message?.content?.trim();

    if (!lyrics) {
      throw new Error('No lyrics generated');
    }

    return lyrics;
  } catch (error) {
    console.error('Error generating lyrics:', error);
    throw new Error('Failed to generate lyrics. Please try again.');
  }
}

server/elevenLabsService.ts.content:
import { ElevenLabsClient } from "@elevenlabs/elevenlabs-js";

const elevenlabs = new ElevenLabsClient({
  apiKey: process.env.ELEVENLABS_API_KEY || "sk_d5323a3d63a97aadc7fa918bab31b4cf830fe8ef6a648d88"
});

export interface MusicGenerationParams {
  tags: string;
  lyrics?: string;
  durationMs: number;
}

export async function generateMusic(params: MusicGenerationParams): Promise<ReadableStream<Uint8Array>> {
  const { tags, lyrics, durationMs } = params;

  // Validate duration
  if (durationMs < 10000 || durationMs > 300000) {
    throw new Error('Duration must be between 10000ms (10s) and 300000ms (5min)');
  }

  // Build prompt
  let prompt = `Create a song with this style: ${tags}`;
  if (lyrics && lyrics.trim()) {
    prompt += ` and these lyrics: ${lyrics}`;
  }

  try {
    const track = await elevenlabs.music.compose({
      prompt,
      musicLengthMs: durationMs,
    });

    return track;
  } catch (error) {
    console.error('ElevenLabs music generation error:', error);
    throw new Error('Failed to generate music with ElevenLabs');
  }
}

