client/src/pages/check-email.tsx.content:
import { useEffect, useState } from "react";
import { useLocation, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { Mail, RefreshCw, Check, ArrowLeft } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

interface CheckEmailPageState {
  email?: string;
  firstName?: string;
}

export default function CheckEmailPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [userInfo, setUserInfo] = useState<CheckEmailPageState>({});
  const [resendingEmail, setResendingEmail] = useState(false);
  const [resendEmail, setResendEmail] = useState("");
  const [emailSent, setEmailSent] = useState(false);

  useEffect(() => {
    // Get email info from navigation state or localStorage
    const navigationState = (window.history.state as CheckEmailPageState) || {};
    const savedEmail = localStorage.getItem("pendingVerificationEmail");
    const savedFirstName = localStorage.getItem("pendingVerificationFirstName");

    setUserInfo({
      email: navigationState.email || savedEmail || "",
      firstName: navigationState.firstName || savedFirstName || ""
    });

    // Set resend email input
    setResendEmail(navigationState.email || savedEmail || "");

    // Clear temporary storage after loading
    if (savedEmail) {
      localStorage.removeItem("pendingVerificationEmail");
      localStorage.removeItem("pendingVerificationFirstName");
    }
  }, []);

  const handleResendEmail = async () => {
    if (!resendEmail.trim()) {
      toast({
        title: "Email Required",
        description: "Please enter your email address to resend verification.",
        variant: "destructive",
      });
      return;
    }

    setResendingEmail(true);
    try {
      await apiRequest("POST", "/api/auth/resend-verification", { 
        email: resendEmail.trim() 
      });

      setEmailSent(true);
      toast({
        title: "Email Sent",
        description: "A new verification email has been sent. Check your inbox.",
      });
    } catch (error: any) {
      toast({
        title: "Failed to Send Email",
        description: error.message || "Could not send verification email. Please try again.",
        variant: "destructive",
      });
    } finally {
      setResendingEmail(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-black via-purple-950/20 to-black p-4">
      <Card className="w-full max-w-md bg-black/50 border-purple-500/20 backdrop-blur-sm">
        <CardHeader className="text-center">
          <div className="mx-auto mb-4 w-16 h-16 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
            {emailSent ? (
              <Check className="w-8 h-8 text-white" />
            ) : (
              <Mail className="w-8 h-8 text-white" />
            )}
          </div>
          <CardTitle className="text-2xl font-bold text-white">
            Check Your Email
          </CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          {userInfo.email && (
            <div className="text-center">
              <p className="text-gray-300 mb-2">
                Hi {userInfo.firstName && userInfo.firstName.trim() !== "" ? userInfo.firstName : "there"}! ðŸ‘‹
              </p>
              <p className="text-gray-300 mb-4">
                We've sent a verification link to:
              </p>
              <p className="font-semibold text-purple-400 bg-purple-500/10 px-4 py-2 rounded-lg border border-purple-500/20">
                {userInfo.email}
              </p>
            </div>
          )}

          <div className="space-y-4">
            <div className="text-sm text-gray-400 space-y-2">
              <p className="flex items-center">
                <span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>
                Click the verification link in your email
              </p>
              <p className="flex items-center">
                <span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>
                Check your spam folder if you don't see it
              </p>
              <p className="flex items-center">
                <span className="w-2 h-2 bg-purple-500 rounded-full mr-3"></span>
                The link expires in 24 hours
              </p>
            </div>

            <div className="border-t border-purple-500/20 pt-4">
              <p className="text-sm text-gray-400 mb-3">
                Didn't receive the email? Enter your email address:
              </p>
              <div className="space-y-3">
                <Input
                  type="email"
                  placeholder="Enter your email address"
                  value={resendEmail}
                  onChange={(e) => setResendEmail(e.target.value)}
                  className="bg-black/50 border-purple-500/30 text-white placeholder:text-gray-500 focus:border-purple-500"
                  data-testid="input-resend-email"
                />
                <Button
                  onClick={handleResendEmail}
                  disabled={resendingEmail}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
                  data-testid="button-resend-verification"
                >
                  {resendingEmail ? (
                    <>
                      <RefreshCw className="w-4 h-4 mr-2 animate-spin" />
                      Sending...
                    </>
                  ) : (
                    <>
                      <Mail className="w-4 h-4 mr-2" />
                      Resend Verification Email
                    </>
                  )}
                </Button>
              </div>
            </div>

            <div className="text-center pt-4 border-t border-purple-500/20">
              <Link href="/">
                <Button variant="ghost" className="text-purple-400 hover:text-purple-300 hover:bg-purple-500/10" data-testid="link-back-home">
                  <ArrowLeft className="w-4 h-4 mr-2" />
                  Back to Home
                </Button>
              </Link>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/verify-email.tsx.content:
import { useEffect, useState } from "react";
import { useLocation, Link } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { CheckCircle, XCircle, Loader2, ArrowRight, RefreshCw } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";

type VerificationState = "loading" | "success" | "error" | "expired";

interface VerificationResult {
  message: string;
  user?: {
    firstName: string;
    email: string;
  };
  expired?: boolean;
}

export default function VerifyEmailPage() {
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [verificationState, setVerificationState] = useState<VerificationState>("loading");
  const [result, setResult] = useState<VerificationResult | null>(null);

  // Get token from URL parameters
  const getTokenFromPath = () => {
    const pathParts = window.location.pathname.split('/');
    const verifyIndex = pathParts.indexOf('verify-email');
    return verifyIndex !== -1 && pathParts[verifyIndex + 1] ? pathParts[verifyIndex + 1] : null;
  };

  useEffect(() => {
    const verifyEmail = async () => {
      const token = getTokenFromPath();

      if (!token) {
        setVerificationState("error");
        setResult({ message: "Invalid verification link. Please check your email for the correct link." });
        return;
      }

      try {
        const response = await fetch(`/api/auth/verify-email/${token}`);
        const data = await response.json();

        if (response.ok) {
          setVerificationState("success");
          setResult(data);
          toast({
            title: "Email Verified!",
            description: "Your account is now active. You can log in.",
          });
        } else {
          if (data.expired) {
            setVerificationState("expired");
          } else {
            setVerificationState("error");
          }
          setResult(data);
        }
      } catch (error) {
        console.error("Verification error:", error);
        setVerificationState("error");
        setResult({ message: "An error occurred during verification. Please try again." });
      }
    };

    verifyEmail();
  }, [toast]);

  const handleResendVerification = () => {
    // Redirect to a page where user can request new verification
    setLocation("/check-email");
  };

  const handleGoToLogin = () => {
    setLocation("/auth");
  };

  const renderContent = () => {
    switch (verificationState) {
      case "loading":
        return (
          <>
            <div className="mx-auto mb-4 w-16 h-16 bg-gradient-to-br from-purple-500 to-pink-500 rounded-full flex items-center justify-center">
              <Loader2 className="w-8 h-8 text-white animate-spin" />
            </div>
            <CardTitle className="text-2xl font-bold text-white text-center">
              Verifying Your Email
            </CardTitle>
            <div className="text-center">
              <p className="text-gray-300">
                Please wait while we verify your email address...
              </p>
            </div>
          </>
        );

      case "success":
        return (
          <>
            <div className="mx-auto mb-4 w-16 h-16 bg-gradient-to-br from-green-500 to-emerald-500 rounded-full flex items-center justify-center">
              <CheckCircle className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl font-bold text-white text-center">
              Email Verified Successfully!
            </CardTitle>
            <div className="space-y-4 text-center">
              <p className="text-gray-300">
                Welcome to NuMusic, {result?.user?.firstName || "there"}! ðŸŽ‰
              </p>
              <p className="text-green-400">
                {result?.message || "Your account is now active and ready to use."}
              </p>
              <Button
                onClick={handleGoToLogin}
                className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
                data-testid="button-go-to-login"
              >
                <ArrowRight className="w-4 h-4 mr-2" />
                Continue to Login
              </Button>
            </div>
          </>
        );

      case "expired":
        return (
          <>
            <div className="mx-auto mb-4 w-16 h-16 bg-gradient-to-br from-orange-500 to-red-500 rounded-full flex items-center justify-center">
              <XCircle className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl font-bold text-white text-center">
              Verification Link Expired
            </CardTitle>
            <div className="space-y-4 text-center">
              <p className="text-gray-300">
                {result?.message || "This verification link has expired or is no longer valid."}
              </p>
              <p className="text-orange-400">
                Don't worry! You can request a new verification email.
              </p>
              <Button
                onClick={handleResendVerification}
                className="w-full bg-gradient-to-r from-orange-600 to-red-600 hover:from-orange-700 hover:to-red-700"
                data-testid="button-request-new-verification"
              >
                <RefreshCw className="w-4 h-4 mr-2" />
                Request New Verification Email
              </Button>
            </div>
          </>
        );

      case "error":
      default:
        return (
          <>
            <div className="mx-auto mb-4 w-16 h-16 bg-gradient-to-br from-red-500 to-pink-500 rounded-full flex items-center justify-center">
              <XCircle className="w-8 h-8 text-white" />
            </div>
            <CardTitle className="text-2xl font-bold text-white text-center">
              Verification Failed
            </CardTitle>
            <div className="space-y-4 text-center">
              <p className="text-gray-300">
                {result?.message || "Something went wrong during email verification."}
              </p>
              <div className="space-y-2">
                <Button
                  onClick={handleResendVerification}
                  className="w-full bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700"
                  data-testid="button-try-again"
                >
                  <RefreshCw className="w-4 h-4 mr-2" />
                  Request New Verification Email
                </Button>
                <Link href="/">
                  <Button variant="ghost" className="w-full text-purple-400 hover:text-purple-300 hover:bg-purple-500/10" data-testid="link-go-home">
                    Back to Home
                  </Button>
                </Link>
              </div>
            </div>
          </>
        );
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-black via-purple-950/20 to-black p-4">
      <Card className="w-full max-w-md bg-black/50 border-purple-500/20 backdrop-blur-sm">
        <CardHeader className="text-center">
          {renderContent()}
        </CardHeader>
      </Card>
    </div>
  );
}

client/src/pages/reset-password.tsx.content:
import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Music, Eye, EyeOff, Lock, CheckCircle, AlertCircle } from "lucide-react";
import backgroundVideo from "/background-video.mp4";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation, Link } from "wouter";
import { z } from "zod";

const resetPasswordSchema = z.object({
  password: z.string().min(8, "Password must be at least 8 characters"),
  confirmPassword: z.string().min(1, "Please confirm your password"),
}).refine((data) => data.password === data.confirmPassword, {
  message: "Passwords don't match",
  path: ["confirmPassword"],
});

type ResetPasswordForm = z.infer<typeof resetPasswordSchema>;

export default function ResetPassword() {
  const [, setLocation] = useLocation();
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [token, setToken] = useState<string>("");
  const [isSuccess, setIsSuccess] = useState(false);
  const { toast } = useToast();

  // Extract token from URL
  useEffect(() => {
    const path = window.location.pathname;
    const tokenMatch = path.match(/\/reset-password\/(.+)$/);
    if (tokenMatch) {
      setToken(tokenMatch[1]);
    } else {
      toast({
        title: "Invalid Reset Link",
        description: "This password reset link is invalid or malformed.",
        variant: "destructive",
      });
      setLocation("/auth");
    }
  }, [setLocation, toast]);

  const resetForm = useForm<ResetPasswordForm>({
    resolver: zodResolver(resetPasswordSchema),
    defaultValues: {
      password: "",
      confirmPassword: "",
    },
  });

  const resetMutation = useMutation({
    mutationFn: async (data: ResetPasswordForm) => {
      const response = await apiRequest("/api/auth/reset-password", "POST", {
        token,
        newPassword: data.password,
      });
      return response.json();
    },
    onSuccess: (result) => {
      setIsSuccess(true);
      toast({
        title: "Password Reset Successful",
        description: result.message || "Your password has been reset successfully.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Reset Failed",
        description: error.message || "Failed to reset password. The link may be expired.",
        variant: "destructive",
      });
    },
  });

  const onResetPassword = (data: ResetPasswordForm) => {
    resetMutation.mutate(data);
  };

  if (isSuccess) {
    return (
      <div className="min-h-screen relative overflow-hidden">
        {/* Background Video */}
        <video
          autoPlay
          muted
          loop
          playsInline
          className="absolute inset-0 w-full h-full object-cover z-0"
        >
          <source src={backgroundVideo} type="video/mp4" />
        </video>

        {/* Content overlay */}
        <div className="relative z-10 bg-black/40 min-h-screen flex items-center justify-center">
          <Card className="w-full max-w-md bg-gray-800/50 border-gray-700 backdrop-blur-sm">
            <CardHeader className="text-center">
              <div className="mx-auto w-16 h-16 bg-green-600 rounded-full flex items-center justify-center mb-4">
                <CheckCircle className="w-8 h-8 text-white" />
              </div>
              <CardTitle className="text-2xl font-bold text-white">Password Reset Complete</CardTitle>
              <CardDescription className="text-gray-400">
                Your password has been successfully changed. You can now sign in with your new password.
              </CardDescription>
            </CardHeader>
            <CardContent className="text-center">
              <Link href="/auth">
                <Button className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white">
                  Go to Sign In
                </Button>
              </Link>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen relative overflow-hidden">
      {/* Background Video */}
      <video
        autoPlay
        muted
        loop
        playsInline
        className="absolute inset-0 w-full h-full object-cover z-0"
      >
        <source src={backgroundVideo} type="video/mp4" />
      </video>

      {/* Content overlay */}
      <div className="relative z-10 bg-black/40 min-h-screen flex items-center justify-center">
        <div className="container mx-auto px-4 py-8">
          <div className="flex justify-center">
            <Card className="w-full max-w-md bg-gray-800/50 border-gray-700 backdrop-blur-sm">
              <CardHeader className="text-center">
                <div className="flex items-center justify-center space-x-3 mb-4">
                  <div className="w-12 h-12 bg-gradient-to-br from-purple-500 to-blue-500 rounded-xl flex items-center justify-center">
                    <Music className="text-white text-xl" />
                  </div>
                  <h1 className="text-2xl font-bold text-white">NuMusic</h1>
                </div>
                <CardTitle className="text-2xl font-bold text-white">Reset Your Password</CardTitle>
                <CardDescription className="text-gray-400">
                  Enter your new password below
                </CardDescription>
              </CardHeader>
              <CardContent>
                <form onSubmit={resetForm.handleSubmit(onResetPassword)} className="space-y-4">
                  <div className="space-y-2">
                    <Label htmlFor="new-password" className="text-gray-300">New Password</Label>
                    <div className="relative">
                      <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                      <Input
                        id="new-password"
                        type={showPassword ? "text" : "password"}
                        placeholder="Enter new password"
                        className="pl-10 pr-10 bg-gray-700 border-gray-600 text-white placeholder-gray-400"
                        data-testid="input-new-password"
                        {...resetForm.register("password")}
                      />
                      <button
                        type="button"
                        onClick={() => setShowPassword(!showPassword)}
                        className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white"
                      >
                        {showPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                      </button>
                    </div>
                    {resetForm.formState.errors.password && (
                      <p className="text-red-400 text-sm">{resetForm.formState.errors.password.message}</p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="confirm-password" className="text-gray-300">Confirm Password</Label>
                    <div className="relative">
                      <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                      <Input
                        id="confirm-password"
                        type={showConfirmPassword ? "text" : "password"}
                        placeholder="Confirm new password"
                        className="pl-10 pr-10 bg-gray-700 border-gray-600 text-white placeholder-gray-400"
                        data-testid="input-confirm-password"
                        {...resetForm.register("confirmPassword")}
                      />
                      <button
                        type="button"
                        onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                        className="absolute right-3 top-1/2 transform -translate-y-1/2 text-gray-400 hover:text-white"
                      >
                        {showConfirmPassword ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                      </button>
                    </div>
                    {resetForm.formState.errors.confirmPassword && (
                      <p className="text-red-400 text-sm">{resetForm.formState.errors.confirmPassword.message}</p>
                    )}
                  </div>

                  <div className="pt-2">
                    <Button
                      type="submit"
                      className="w-full bg-gradient-to-r from-purple-600 to-blue-600 hover:from-purple-700 hover:to-blue-700 text-white"
                      disabled={resetMutation.isPending}
                      data-testid="button-reset-password"
                    >
                      {resetMutation.isPending ? "Resetting..." : "Reset Password"}
                    </Button>
                  </div>

                  <div className="text-center pt-4">
                    <Link href="/auth">
                      <button className="text-sm text-purple-400 hover:text-purple-300 underline">
                        Back to Sign In
                      </button>
                    </Link>
                  </div>
                </form>
              </CardContent>
            </Card>
          </div>
        </div>
      </div>
    </div>
  );
}

